import { useState } from 'react';
import Banner from './components/Banner'
import Club from './components/Club';
import FormMain from './components/FormMain'

function App() {

  const clubs = [
    {
      name: 'Palmeiras',
      primaryColor: '#D9F7E9',
      secondColor: '#57C278'
    },
    {
      name: 'Corinthians',
      primaryColor: '',
      secondColor: ''
    },
    {
      name: 'São Paulo',
      primaryColor: '',
      secondColor: ''
    },
    {
      name: 'Santos',
      primaryColor: '',
      secondColor: ''
    }
]

  const [players, setPlayers] = useState([])

  const savePlayer = (player) => {
    console.log(player)
    setPlayers([...players, player])
  }

  return (
    <div className="App">
      <Banner />
      <FormMain registerPlayer={player => savePlayer(player)} />
      {clubs.map(club => <Club name={club.name} />)}
      {/* <Club name='Palmeiras' />
      <Club name='Corinthians' />
      <Club name='São Paulo' />
      <Club name='Santos' /> */}
    </div>
  );
}

export default App;
