import { useState } from "react"
import Button from "../Button"
import DropDownList from "../DropDownList"
import TextFieldForm from "../TextFieldForm"
import './FormMain.css'

const FormMain = (props) => {

    const positions = [
        'Selecione a posição',
        'Goleiro',
        'Zagueiro',
        'Lateral-direito',
        'Lateral-esquerdo',
        'Meio-campo',
        'Atacante'
    ]

    const [name, setName] = useState('')
    const [position, setPosition] = useState('')
    const [image, setImage] = useState('')
    const [club, setClub] = useState('')

    const saveData = (events) => {
        events.preventDefault()
        props.registerPlayer({
            name,
            position,
            image,
            club
        })
        // console.log('Formulário salvo: ' + name, position, image, club)
    }

    return (
        <section className="form-main">
            <form onSubmit={saveData}>
            <h2>Preencha os dados para criar o card do jogador</h2>
                <TextFieldForm 
                    required={true} 
                    label='Nome' 
                    placeholder='Digite o nome'
                    value={name}
                    changeValue={value => setName(value)} />
                <DropDownList 
                    label='Posição' 
                    items={positions}
                    value={position}
                    changeValue={value => setPosition(value)} />
                <TextFieldForm 
                    required={true} 
                    label='Imagem' 
                    placeholder='Digite o endereço da imagem'
                    value={image}
                    changeValue={value => setImage(value)} />
                <DropDownList 
                    label='Clube' 
                    items={props.clubs}
                    value={club}
                    changeValue={value => setClub(value)} />
                <Button>
                    Criar Card
                </Button>
            </form>
        </section>
    )
}

export default FormMain
